<html>	<head>		<title>浏览商品类别</title>		<style>			body{color:teal;}			table{border:1px solid teal;}			th,td{align:center;}			a{text-decoration:none;}		</style>		<meta http-equiv="Content-Type" content="text/html;charset=utf-8"/>	</head>	<body bgcolor='bisque'>		<center>			<h2>浏览商品类别</h2>			<hr/>			<table width="800" border='1' cellspacing='0' cellpadding='0'>				<tr>					<td>ID</td>					<td>类别名称</td>					<td>类别父ID</td>					<td>类别路径</td>					<td>操作</td>				</tr>						<?php				/* 浏览商品类别 */				//导入数据库配置				require("../../public/dbconfig.php");								//连接数据库并判断				$link=@mysql_connect(HOST,USER,PASS) or die("连接失败,原因".mysql_error());								//选择数据库设置字符集				mysql_select_db(DBNAME,$link);				mysql_set_charset("utf8");								//sql语句并执行				$sql="select * from type order by concat(path,id) asc";				$result=mysql_query($sql,$link);				if($result && mysql_num_rows($result)>0){				while($row=mysql_fetch_assoc($result)){					echo "<tr>";					echo "<td>{$row['id']}</td>";					echo "<td>{$row['name']}</td>";					echo "<td>{$row['pid']}</td>";					echo "<td>{$row['path']}</td>";					echo "<td><a href='action.php?action=del&id={$row['id']}'>删除</a>								<a href='./modify.php?k={$row['id']}'>更改</a>								<a href='add.php?pid={$row['id']}&name={$row['name']}&path={$row['path']}'>添加子类</a>						  </td>";					echo "</tr>";					};				}else{					echo "浏览失败,原因".mysql_error();				}								?>			</table>			<br/>			/* <a href='action.php?action=xiaoyan'>校正ID递增值(测试使用)</a> */			<br>			<br/>			<?			switch($_GET['e']){					//添加是否成功					case "2":echo "<span style='color:red'>成功</span>";					break;					case "3":echo "<span style='color:red'>失败</span>";					break;					case "4":echo "<span style='color:red'>成功</span>";					break;					case "5":echo "<span style='color:red'>失败</span>";					break;				}				?>			<br/>								</center>	</body></html>