<html>	<head>		<title>浏览商品类别</title>		<meta http-equiv="Content-Type" content="text/html;charset=utf-8"/>	</head>	<body bgcolor='bisque'>		<center>			<h2>浏览商品类别</h2>			<hr/>						<select name='tid'>			<?php				/* 浏览商品类别 */				//导入数据库配置				require("../../public/dbconfig.php");								//连接数据库并判断				$link=@mysql_connect(HOST,USER,PASS) or die("连接失败,原因".mysql_error());								//选择数据库设置字符集				mysql_select_db(DBNAME,$link);				mysql_set_charset("utf8");								//sql语句并执行				$sql="select * from type order by concat(path,id) asc";				$result=mysql_query($sql,$link);							while($row=mysql_fetch_assoc($result)){										$m=substr_count($row['path'],",")-1;					$strpad=str_pad('',$m*12,"&nbsp;");					$disabled=($row['pid']==0)?"disabled":"";					echo "<option value='{$row['id']}' {$disabled}>{$strpad}{$row['name']}</option>";				}													//释放结果集，关闭连接				  mysql_free_result($result);				  mysql_close($link);				?>			</select>													</center>	</body></html>