<?php session_start(); ?><html>	<head>		<title>浏览用户</title>		<style>			body{color:teal;}			a{text-decoration:none;}				</style>	</head>	<body bgcolor='bisque'>		<center>			<h2>浏览用户</h2>			<table width="1000" border="1" cellspacing="0">				<tr>					<th>编号</th>					<th>账号</th>					<th>真实姓名</th>					<th>性别</th>					<th>地址</th>					<th>邮编</th>					<th>电话号码</th>					<th>Email</th>					<th>状态</th>					<th>注册时间</th>					<th>操作</th>				</tr>			<?php				$sex=array(0=>'男',1=>'女');				$state=array(0=>'禁用',1=>'开启',2=>'后台管理员');			/* 浏览用户 */				//导入数据库配置				require("../../public/dbconfig.php");								//连接数据库并判断				$link=@mysql_connect(HOST,USER,PASS) or die("连接失败,原因".mysql_error());								//选择数据库设置字符集				mysql_select_db(DBNAME,$link);				mysql_set_charset("utf8");		//=========数据分页处理=======================				$page=isset($_GET['p'])?$_GET['p']:1;//获取当前页				$pagesize=5;				$maxpage=0;				$maxrows=0;		//计算最大的页数				$sql="select count(*) from users";				$result=mysql_query($sql,$link);				$maxrows=mysql_result($result,0,0);//获取数据的条数		//获取最大页数
				$maxpage=ceil($maxrows/$pagesize);		//判断页号是否越界
			if($page>$maxpage){				$page=$maxpage;				}			if($page<1){				$page=1;				}		//拼装分页sql语句（limit 【当前页-1】*页大小，【页大小】）				$limit=" limit ".($page-1)*$pagesize.",".$pagesize;																						//=========================================						//sql语句并执行				$sql="select * from users".$limit;				$result=mysql_query($sql,$link);			//解析结果集				if($result && mysql_num_rows($result)>0){					while($row=mysql_fetch_assoc($result)){									echo "<tr>";					echo "<td>{$row['id']}</td>";					echo "<td>{$row['username']}</td>";					echo "<td>{$row['name']}</td>";					echo "<td>{$sex[$row['sex']]}</td>";					echo "<td>{$row['address']}</td>";					echo "<td>{$row['code']}</td>";					echo "<td>{$row['phone']}</td>";					echo "<td>{$row['email']}</td>";					echo "<td>{$state[$row['state']]}</td>";					echo "<td>".date("Y-m-d H:i:s",$row['addtime']+8*3600)."</td>";					echo "<td><a href='action.php?action=del&k={$row['id']}'>删除</a>								<a href='edit.php?k={$row['id']}'>修改</a>							</td>";					echo "</tr>";}				}			?>			</table>			<br><br>		<?php		//===================输出分页信息=======		echo "当前页{$page}/{$maxpage}  共{$maxrows}条";		echo "&nbsp;";		echo "<a href='index1.php?p=1'>首页|</a>";		echo "&nbsp;";		echo "<a href='index1.php?p=".($page+1)."'>下一页|</a>";		echo "&nbsp;";		echo "<a href='index1.php?p=".($page-1)."'>上一页</a>";		echo " <a href='index3.php?p={$maxpage}'>末页</a> ";						//=======================================		?>		</center>	</body>	</html>